        //                             <label className="form-check-label" htmlFor="check-transportador">Transportador</label>
        //                         </div>
        //                     </div>
                            
        //                     <button id="submit" type="submit" name="submit" className="btn" onClick={handler}>Registar</button>
        //                 </form>
        //             </div>
        //         </div>
        //     </div>
        //     {/* MODAL */}
