import React, { useState } from "react";
import Axios from "axios";
import $ from "jquery";

function Login() {

    // states for login
    const [email, setEmail]         = useState('');
    const [password, setPassword]   = useState('');

    const handleShow = () => {
        $("#modal_login").css("display", "block");
    }

    const handleHide = () => {
        $("#modal_login").css("display", "none")
    }

    const goHome = () => {
        window.location.href = "http://localhost:3000/";
    }

    const handler = (x) => {
        switch(x.target.name) {
            case "email":
                setEmail(x.target.value);
                break;
            case "password":
                setPassword(x.target.value);
                break;
            case "submit":
                x.preventDefault();
                if ( email === '' || password === '') {
                        // setError(true);
                } else {
                    Axios.get("http://localhost:3001/api/login", {
                        email: email,
                        pwd: password
                    }).then((response) => {
                        console.log(response);
                        if (response.data === "success") {

                        } else {

                        }
                        handleShow();
                    })
                }
                break;
            default:
                console.log();
        }
    }

    // showing error message if error true
    const errorMessage = () => {
        return (
            <div className="error" style={{display: error ? '' : 'none'}}>
                <h1>Please enter all the fields</h1>
            </div>
        );
    };

    return (
        <div className="col-8 justify-content-center mt-5">
            <div class="form-holder">
                <div class="form-content position-absolute">
                    <div class="form-items">
                        <h3>LOGIN</h3>
                        <p>Inicie sessรฃo aqui.</p>

                        <div className="messages">
                            {errorMessage()}
                        </div>

                        <form>
                            <div class="col-md-12">
                                <label>Email: </label>
                                <input class="form-control" type="email" name="email" onChange={handler} required />
                            </div>
                            <div class="col-md-12">
                                <label>Password: </label>
                                <input class="form-control" type="password" name="password" onChange={handler} required />
                            </div>
                            
                            <button id="submit" type="submit" name="submit" onClick={handler} class="btn">Iniciar Sessรฃo</button>
                        </form>
                    </div>
                </div>
            </div>
        </div>
    );
}

export default Login;
