import React, { useState } from "react";
import Axios from "axios";

function Register() {

    const [nome, setNome]           = useState('');
    const [email, setEmail]         = useState('');
    const [nif, setNif]             = useState('');
    const [telem, setTelem]         = useState('');
    const [password, setPassword]   = useState('');
    const [checkPassword, setCheckPassword]   = useState('');

    const register = () => {
        Axios.post("http://localhost:3000/register", {
            nome: nome,
            email: email,
            nif: nif,
            telem: telem,
            password: password,
            checkPassword: checkPassword
        }).then((response) => {
            console.log(response);
        });
    };

    const handleInput = (x) => {
        const {id, value} = x.target;

        if (id === "nome") {
            setNome(value);
        }

        if (id === "email") {
            setEmail(value);
        }

        if (id === "nif") {
            setNif(value);
        }

        if (id === "telem") {
            setTelem(value);
        }

        if (id === "password") {
            setPassword(value);
        }

        if (id === "checkPassword") {
            setCheckPassword(value);
        }
    }

    return (
        <div className="col-8 justify-content-center mt-5">
            <div className="form-holder">
                <div className="form-content position-absolute" style={{margin: "-70px 0 0 0"}}>
                    <div className="form-items">
                        <h3>REGISTO</h3>
                        <p>Registe-se aqui.</p>
                        <form className="" method="post">
                            <div className="col-md-12">
                                <input className="form-control" type="text" name="nome" placeholder="Nome Completo" onChange={handleInput} required />
                            </div>
                            <div className="col-md-12">
                                <input className="form-control" type="email" name="email" placeholder="Email" onChange={handleInput} required />
                            </div>
                            <div className="col-md-12">
                                <input className="form-control" type="text" name="nif" placeholder="NIF" onChange={handleInput} required />
                            </div>
                            <div className="col-md-12">
                                <input className="form-control" type="text" name="telem" placeholder="Número de Telemóvel" onChange={handleInput} required />
                            </div>
                            <div className="col-md-12">
                                <input className="form-control" type="password" name="password" placeholder="Password" onChange={handleInput} required />
                            </div>
                            <div className="col-md-12">
                                <input className="form-control" type="password" name="checkPassword" placeholder="Confirme a Password" onChange={handleInput} required />
                            </div>
                            <div className="col-md-12">
                                <div className="form-check">
                                    <input className="form-check-input" type="checkbox" id="check-consumidor" name="tipo" value="consumidor" />
                                    <label className="form-check-label" for="check-consumidor">Consumidor</label>
                                    <br />
                                    <input className="form-check-input" type="checkbox" id="check-fornecedor" name="tipo" value="fornecedor" />
                                    <label className="form-check-label" for="check-fornecedor">Fornecedor</label>
                                    <br />
                                    <input className="form-check-input" type="checkbox" id="check-transportador" name="tipo" value="transportador" />
                                    <label className="form-check-label" for="check-transportador">Transportador</label>
                                </div>
                            </div>
                            
                            <button id="submit" type="submit" className="btn" onClick={register}>Registar</button>
                        </form>
                    </div>
                </div>
            </div>
        </div>
    );
}

export default Register;
